import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Publication from '../Publication/Publication';
import Counter from '../Counter/Counter';
import Controls from '../Controls/Controls';
import styled4 from './Reader.module.css';

class Reader extends Component {
  constructor(props, items) {
    super(props, items);

    this.state = {
      publication: this.props.items[0],
      total: this.props.items.length,
      value: 1,
      butPrev: true,
      butNext: false,
    };
  }

  handlPrevArticle = () => {
    this.setState(prevState => ({
      publication: this.props.items[prevState.value - 1],
      value: prevState.value - 1,
      butPrev: prevState.value === 1 && !prevState.butPrev,
      butNext:
        prevState.value === this.props.items.length && !prevState.butNext,
    }));
  };

  handlNextArticle = () => {
    this.setState(prevState => ({
      publication: this.props.items[prevState.value + 1],
      value: prevState.value + 1,
      butPrev: prevState.value === 1 && !prevState.butPrev,
      butNext:
        prevState.value === this.props.items.length - 1 && !prevState.butNext,
    }));
  };

  render() {
    const { publication, total, value, butPrev, butNext } = this.state;

    return (
      <div className={styled4.containerReader}>
        <Publication publication={publication} />
        <Counter value={value} total={total} />
        <Controls
          butPrev={butPrev}
          butNext={butNext}
          handlPrevArticle={this.handlPrevArticle}
          handlNextArticle={this.handlNextArticle}
        />
      </div>
    );
  }
}

Reader.propTypes = {
  items: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.string.isRequired,
      title: PropTypes.string.isRequired,
      text: PropTypes.string.isRequired,
    }).isRequired,
  ).isRequired,
};

export default Reader;
