import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Publication from '../Publication/Publication';
import Counter from '../Counter/Counter';
import Controls from '../Controls/Controls';
import styled4 from './Reader.module.css';

export default class Reader extends Component {
  static propTypes = {
    id: PropTypes.isRequired,
    title: PropTypes.isRequired,
    text: PropTypes.isRequired,
  };

  state = {
    publication: this.items[0],
    total: this.items.length,
    value: 1,
    butPrev: true,
    butNext: false,
  };

  handlPrevArticle = () => {
    this.setState(prevState => ({
      publication: this.items[prevState.value - 2],
      value: prevState.value - 1,
      butPrev: prevState.value === 2 && !prevState.butPrev,
      butNext: prevState.value === this.items.length && !prevState.butNext,
    }));
  };

  handlNextArticle = () => {
    this.setState(prevState => ({
      publication: this.items[prevState.value],
      value: prevState.value + 1,
      butPrev: prevState.value === 1 && !prevState.butPrev,
      butNext: prevState.value === this.items.length - 1 && !prevState.butNext,
    }));
  };

  render() {
    const { publication, total, value, butPrev, butNext } = this.state;

    return (
      <div className={styled4.containerReader}>
        <Publication publication={publication} />{' '}
        <Counter value={value} total={total} />{' '}
        <Controls
          butPrev={butPrev}
          butNext={butNext}
          handlPrevArticle={this.handlPrevArticle}
          handlNextArticle={this.handlNextArticle}
        />{' '}
      </div>
    );
  }
}
